    /**
       * 时间戳格式化函数
       * @param  {string} format    格式
       * @param  {int}    timestamp 要格式化的时间 默认为当前时间
       * @return {string}           格式化的时间字符串
       */
    date(format, timestamp) {
        let a; const
            jsdate = ((timestamp) ? new Date(timestamp * 1000) : new Date());
        const pad = function (n, c) {
            if ((n += '').length < c) {
                return new Array(++c - n.length).join('0') + n;
            }
            return n;
        };
        const txt_weekdays = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const txt_ordin = {
            1: 'st',
            2: 'nd',
            3: 'rd',
            21: 'st',
            22: 'nd',
            23: 'rd',
            31: 'st',
        };
        const txt_months = ['', 'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
        var f = {
            // Day
            d() {
                return pad(f.j(), 2);
            },
            D() {
                return f.l().substr(0, 3);
            },
            j() {
                return jsdate.getDate();
            },
            l() {
                return txt_weekdays[f.w()];
            },
            N() {
                return f.w() + 1;
            },
            S() {
                return txt_ordin[f.j()] ? txt_ordin[f.j()] : 'th';
            },
            w() {
                return jsdate.getDay();
            },
            z() {
                return (jsdate - new Date(`${jsdate.getFullYear()}/1/1`)) / 864e5 >> 0;
            },

            // Week
            W() {
                const a = f.z();
                const b = 364 + f.L() - a;
                let nd2; const
                    nd = (new Date(`${jsdate.getFullYear()}/1/1`).getDay() || 7) - 1;
                if (b <= 2 && ((jsdate.getDay() || 7) - 1) <= 2 - b) {
                    return 1;
                } if (a <= 2 && nd >= 4 && a >= (6 - nd)) {
                    nd2 = new Date(`${jsdate.getFullYear() - 1}/12/31`);
                    return date('W', Math.round(nd2.getTime() / 1000));
                }
                return (1 + (nd <= 3 ? ((a + nd) / 7) : (a - (7 - nd)) / 7) >> 0);
            },

            // Month
            F() {
                return txt_months[f.n()];
            },
            m() {
                return pad(f.n(), 2);
            },
            M() {
                return f.F().substr(0, 3);
            },
            n() {
                return jsdate.getMonth() + 1;
            },
            t() {
                let n;
                if ((n = jsdate.getMonth() + 1) == 2) {
                    return 28 + f.L();
                } if (n & 1 && n < 8 || !(n & 1) && n > 7) {
                    return 31;
                }
                return 30;
            },

            // Year
            L() {
                const y = f.Y();
                return (!(y & 3) && (y % 1e2 || !(y % 4e2))) ? 1 : 0;
            },
            // o not supported yet
            Y() {
                return jsdate.getFullYear();
            },
            y() {
                return (`${jsdate.getFullYear()}`).slice(2);
            },

            // Time
            a() {
                return jsdate.getHours() > 11 ? 'pm' : 'am';
            },
            A() {
                return f.a().toUpperCase();
            },
            B() {
                // peter paul koch:
                const off = (jsdate.getTimezoneOffset() + 60) * 60;
                const theSeconds = (jsdate.getHours() * 3600) + (jsdate.getMinutes() * 60) + jsdate.getSeconds() + off;
                let beat = Math.floor(theSeconds / 86.4);
                if (beat > 1000) beat -= 1000;
                if (beat < 0) beat += 1000;
                if ((String(beat)).length == 1) beat = `00${beat}`;
                if ((String(beat)).length == 2) beat = `0${beat}`;
                return beat;
            },
            g() {
                return jsdate.getHours() % 12 || 12;
            },
            G() {
                return jsdate.getHours();
            },
            h() {
                return pad(f.g(), 2);
            },
            H() {
                return pad(jsdate.getHours(), 2);
            },
            i() {
                return pad(jsdate.getMinutes(), 2);
            },
            s() {
                return pad(jsdate.getSeconds(), 2);
            },
            // u not supported yet

            // Timezone
            // e not supported yet
            // I not supported yet
            O() {
                let t = pad(Math.abs(jsdate.getTimezoneOffset() / 60 * 100), 4);
                if (jsdate.getTimezoneOffset() > 0) t = `-${t}`;
                else t = `+${t}`;
                return t;
            },
            P() {
                const O = f.O();
                return (`${O.substr(0, 3)}:${O.substr(3, 2)}`);
            },
            // T not supported yet
            // Z not supported yet

            // Full Date/Time
            c() {
                return `${f.Y()}-${f.m()}-${f.d()}T${f.h()}:${f.i()}:${f.s()}${f.P()}`;
            },
            // r not supported yet
            U() {
                return Math.round(jsdate.getTime() / 1000);
            },
        };

        return format.replace(/[\\]?([a-zA-Z])/g, (t, s) => {
            if (t != s) {
                // escaped
                ret = s;
            } else if (f[s]) {
                // a date function exists
                ret = f[s]();
            } else {
                // nothing special
                ret = s;
            }
            return ret;
        });
    },
    // 返回当前日期精确到秒
    getDay() {
        const year = new Date().getFullYear();
        let month = new Date().getMonth() + 1;
        let day = new Date().getDate();
        let hours = new Date().getHours();
        let mm = new Date().getMinutes();
        let ss = new Date().getSeconds();

        if (month > 0 && month < 10) {
            month = `0${month}`;
        }

        if (day > 0 && day < 10) {
            day = `0${day}`;
        }

        if (hours > 0 && hours < 10) {
            hours = `0${hours}`;
        }

        if (mm > 0 && mm < 10) {
            mm = `0${mm}`;
        }
        if (ss > 0 && ss < 10) {
            ss = `0${ss}`;
        }

        // myDate = year + '' + month + '' + day + '' + hours+''+mm+''+ss;
        // console.log(year + '' + month + '' + day + '' + hours+''+mm+''+ss)

        return `${year}${month}${day}${hours}${mm}${ss}`;
    },
    // 获取返回链接的参数对象
    getUrlData() {
        url = window.location.href; // 获取当前页面的url
        // console.log(url)
        if (url.indexOf('?uid') == -1) {
            arr = url;
            // console.log('没找到');
        } else {
            arr = url.split('?')[1].split('&');
            // console.log('找到了');
            const enUrl = decodeURI(url); // 解码
            const len = enUrl.length; // 获取url的长度值
            const a = enUrl.indexOf('?'); // 获取第一次出现？的位置下标
            const b = enUrl.substr(a + 1, len); // 截取问号之后的内容
            const c = b.split('&'); // 从指定的地方将字符串分割成字符串数组
            var arr = new Array(); // 新建一个数组
            for (let i = 0; i < c.length; i++) {
                const d = c[i].split('=')[1]; // 从=处将字符串分割成字符串数组,并选择第2个元素
                arr.push(d); // 将获取的元素存入到数组中
            }

            return arr;
        }
    },
